package pm1;

public class Exe03 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        byte a=4;
        int b=5;
        float c=6.0f;
        double e=7;
        long d=9;
        System.out.println("byte a="+a+"  "+"int b="+b+"  "+"float c="+c+"  "+"double d="+d+"  "+"long e="+e);
        System.out.println("byte+int:a+b="+(a+b));
        System.out.println("float-int:c-a="+(c-a));
        System.out.println("float*double:c*e="+(c*e));
        System.out.println("double/long:e/d="+(e/d));
        
	}

}
